import './App.css';
import TeamBuilder from "./Component/TeamBuilder";
import FilterBar from "./Component/FilterBar";
import PokemonList from "./Component/PokemonList";
import PokeDetails from "./Component/PokeDetails";
import {CgPokemon} from "react-icons/cg"
import {useState} from "react";
import {starterData} from "./data/starterData";

function App() {

    const [pokemonData, setPokemonData] = useState(starterData);
    const [currentPokemon, setCurrentPokemon] = useState({});
    const [currentTeam, setCurrentTeam] = useState([]);

    return (
        <div className="App">
            <header className={"header"}>
                <div className={"title"}>
                    <CgPokemon/>
                    Pok√©Builder
                </div>
                <div className={"navBar"}>
                    <div>Build</div>
                    <div>Teams</div>
                </div>
            </header>
            <div className={"content"}>
                <TeamBuilder currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>
                <div className={"pokePickModal"}>
                    <FilterBar pokemonData={pokemonData} setPokemonData={setPokemonData}/>
                    <PokemonList pokemonData={pokemonData} setCurrentPokemon={setCurrentPokemon} currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>
                </div>
                {currentPokemon.name &&
                <PokeDetails currentPokemon={currentPokemon} setCurrentPokemon={pokemon => setCurrentPokemon(pokemon)}/>
                }
            </div>
        </div>
    );
}

export default App;
