import "./PokemonList.css";
import Pokemon from "./Pokemon";
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";
import {debounce} from "chart.js/helpers";

const PokemonList = ({pokemonData, setCurrentPokemon, currentTeam, setCurrentTeam}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [viewingPokemon, setViewingPokemon] = useState(pokemonData.slice(0, 20));

    const pokeList = viewingPokemon.map(pokemon => {
        return <Pokemon key={pokemon.id} pokemon={pokemon} setCurrentPokemon={setCurrentPokemon}
                        currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>;
    });

    useEffect(() => {
        document.getElementById("PokemonList").addEventListener("scroll", debounce(() => {
            const scrollList = document.getElementById("PokemonList");

            if (pageNumber <= 44) {
                if (scrollList.scrollTop + scrollList.clientHeight >= scrollList.scrollHeight * .9) {
                    console.log("Scroll top: " + scrollList.scrollTop);
                    console.log("Scroll height: " + scrollList.scrollHeight);
                    setPageNumber(pageNumber => pageNumber + 1);
                }
            }
        }, 100));
    }, []);

    useEffect(() => {
        setViewingPokemon(pokemonData.slice(0, pageNumber * 20));
        console.log("Page number: " + pageNumber);
    }, [pageNumber]);

    return (
        <div id={"PokemonList"}>
            {pokeList}
        </div>
    )
}


export default PokemonList;
