import "./PokemonList.css";
import Pokemon from "./Pokemon";
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";

const PokemonList = ({pokemonData, setCurrentPokemon, currentTeam, setCurrentTeam}) => {

    const [pageNumber, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);

    const pokeList = pokemonData.map(pokemon => {
        return <Pokemon key={pokemon.id} pokemon={pokemon} setCurrentPokemon={setCurrentPokemon}
                        currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>;
    });

    useEffect(() => {
        document.getElementById("PokemonList").addEventListener("scroll", () => {
            if(document.getElementById("PokemonList").scrollTop > document.getElementById("PokemonList").scrollHeight * .75){
                if (hasMore){
                setCurrentPokemon(starterData.slice(pageNumber * 50, (pageNumber * 50) + 50));
                setPage(pageNumber + 1);
                }
            }
        });
    }, []);

    return (
        <div id={"PokemonList"}>
            {pokeList}
        </div>
    )
}


export default PokemonList;
