import "./PokemonList.css";
import Pokemon from "./Pokemon";
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";

const PokemonList = ({pokemonData, setPokemonData, setCurrentPokemon, currentTeam, setCurrentTeam}) => {

    const pokeList = pokemonData.map(pokemon => {
        return <Pokemon key={pokemon.id} pokemon={pokemon} setCurrentPokemon={setCurrentPokemon}
                        currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>;
    });

    useEffect(() => {
        document.getElementById("PokemonList").addEventListener("scroll", () => {
            if (document.getElementById("PokemonList").scrollTop > document.getElementById("PokemonList").scrollHeight * .9) {
                if (pokemonData.size <= 850) {
                    console.log(pageNumber);
                    setPokemonData(starterData.slice(0, pokemonData.size + 50));
                } else {
                    setPokemonData(starterData);
                }
            }
        });
    }, []);

    return (
        <div id={"PokemonList"}>
            {pokeList}
        </div>
    )
}


export default PokemonList;
