import "./FilterBar.css"
import {useState} from "react";

const FilterBar = ({pokemonList, setPokemonList}) => {

    const [filterString, setFilterString] = useState("");

    const handleNameFilter = (event, filterString) => {
        if (event.key === 'Enter') {
            setPokemonList(pokemonList.filter(pokemon => pokemon.name.includes(filterString)));
        }
    }

    return  (
      <div className={"filterBar"}>
          <input
              className={"nameSearch"}
              type={"text"}
              value={filterString}
              onChange={event => setFilterString(event.target.value)}
              onKeyPress={(event, filterString) => handleNameFilter(event, filterString)}/>
      </div>
    );
}

export default FilterBar;
