import './App.css';
import TeamBuilder from "./Component/TeamBuilder";
import FilterBar from "./Component/FilterBar";
import PokemonList from "./Component/PokemonList";
import {CgPokemon} from "react-icons/cg"
import {useEffect, useState} from "react";
import axios from "axios";

function App() {

    const [pokemonList, setPokemonList] = useState([]);
    const [isAdding, setIsAdding] = useState(false);

    useEffect(() => {
        fetchPokemon(1, 300);
    }, []);

    // useEffect(() => {
    //     console.log(pokemonList);
    // }, [pokemonList])

    const fetchPokemon = (start, count) => {
        axios.get("http://localhost:8080/pokemon?start=" + start + "&count=" +count)
            .then(pokemonDTOList => setPokemonList(pokemonDTOList.data));
    }

    return (
        <div className="App">
            <header>
                <div className={"title"}>
                    <CgPokemon/>
                    Pok√©Builder
                </div>
            </header>
            <div className={"content"}>
                <TeamBuilder setIsAdding={setIsAdding}/>
                {isAdding &&
                <div className={"pokePickModal"}>
                    <FilterBar pokemonList={pokemonList} setPokemonList={(pokemonList) => setPokemonList(pokemonList)}/>
                    <PokemonList pokemonList={pokemonList}/>
                </div>
                }
            </div>
        </div>
    );
}

export default App;
