import "./Pokemon.css";
import {CgPokemon} from "react-icons/cg"

const Pokemon = ({pokemon}) => {

    const getTypes = async () => {
        const response = await axios.get("https://pokeapi.co/api/v2/pokemon/" + pokemon.id);
        let typeList = [];

        response.data.types.map(typeSlot => {
            typeList.push(typeSlot.type.name);
        })
        return typeList;
    }

    return (
        <div className={"Pokemon"}>
            <div className={"number"}><CgPokemon/>{pokemon.id} </div>
            <img className={"pokemonImage"} src={pokemon.imageURL} alt={pokemon.name}/>
            <div className={"pokeName"}> {pokemon.name[0].toUpperCase() + pokemon.name.slice(1)}</div>
            <div>{getTypes()}</div>
        </div>
    )
}

export default Pokemon;
