import "./FilterBar.css"
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";
import {MdOutlineFilterList} from "react-icons/md";

const FilterBar = ({pokemonData, setPokemonData}) => {

    const [filterString, setFilterString] = useState("");
    const [selectedValue, setSelectedValue] = useState("all");
    const [selectedType, setSelectedType] = useState("all");

    useEffect(() => {
        setPokemonData(starterData.filter(pokemon => pokemon.name.includes(filterString.toLowerCase())));
    }, [filterString]);

    useEffect(() => {
        if (selectedValue === "all") {
            setPokemonData(starterData);
        }
        if (selectedValue === "I") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 1 && pokemon.id <= 151)));
        }
        if (selectedValue === "II") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 152 && pokemon.id <= 251)));
        }
        if (selectedValue === "III") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 252 && pokemon.id <= 386)));
        }
        if (selectedValue === "IV") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 387 && pokemon.id <= 493)));
        }
        if (selectedValue === "V") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 494 && pokemon.id <= 649)));
        }
        if (selectedValue === "VI") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 650 && pokemon.id <= 721)));
        }
        if (selectedValue === "VII") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 722 && pokemon.id <= 809)));
        }
        if (selectedValue === "VIII") {
            setPokemonData(pokemonData.filter(pokemon => (pokemon.id >= 810 && pokemon.id <= 898)));
        }
    }, [selectedValue]);

    const handleFilterChange = event => {
        setFilterString(event.target.value);
    }

    const handleResetFilter = () => {
        setPokemonData(starterData);
        setFilterString("");
        setSelectedValue("all");
        setSelectedType("all");
    }

    const handleSelectValueChanged = event => {
        setSelectedValue(event.target.value);
    }

    const handleTypeValueChanged = event => {
        setSelectedType(event.target.value);
        if (event.target.value === "all") {
            setPokemonData(starterData);
        } else {
            setPokemonData(pokemonData.filter(pokemon => pokemon.types.map(typeSlot => typeSlot.type.name).includes(event.target.value)));
        }

    }

    return (
        <div className={"FilterBar"}>
            <div className={"filterRow"}>
                <MdOutlineFilterList className={"filterIcon"}/>
                Filter
            </div>
            <div className={"filterRow"}>
                Name:
                <input
                    className={"filterInput"}
                    type={"text"}
                    value={filterString}
                    onChange={event => handleFilterChange(event)}
                />
            </div>
            <div className={"filterRow"}>
                Generation:
                <select className={"filterInput"} value={selectedValue}
                        onChange={event => handleSelectValueChanged(event)}>
                    <option>all</option>
                    <option>I</option>
                    <option>II</option>
                    <option>III</option>
                    <option>IV</option>
                    <option>V</option>
                    <option>VI</option>
                    <option>VII</option>
                    <option>VIII</option>
                </select>
            </div>
            <div className={"filterRow"}>
                Type:
                <select className={"filterInput"} value={selectedType}
                        onChange={event => handleTypeValueChanged(event)}>
                    <option>all</option>
                    <option>bug</option>
                    <option>dark</option>
                    <option>dragon</option>
                    <option>electric</option>
                    <option>fairy</option>
                    <option>fire</option>
                    <option>fighting</option>
                    <option>flying</option>
                    <option>ghost</option>
                    <option>grass</option>
                    <option>ground</option>
                    <option>ice</option>
                    <option>normal</option>
                    <option>poison</option>
                    <option>psychic</option>
                    <option>rock</option>
                    <option>steel</option>
                    <option>water</option>
                </select>
            </div>
            <button className={"resetButton"} onClick={() => handleResetFilter()}>Reset</button>
        </div>
    );
}

export default FilterBar;
