import './App.css';
import TeamBuilder from "./Component/TeamBuilder";
import FilterBar from "./Component/FilterBar";
import PokemonList from "./Component/PokemonList";
import {CgPokemon} from "react-icons/cg"

import {useEffect, useState} from "react";
import axios from "axios";

function App() {

    const [pokemonList, setPokemonList] = useState([]);
    const [isAdding, setIsAdding] = useState(false);

    useEffect(() => {
        fetchPokemon();
    }, []);

    useEffect(() => {
        console.log(pokemonList);
    }, [pokemonList])

    const fetchPokemon = async () => {
        const pokemonListResponse = await axios.get("https://pokeapi.co/api/v2/pokemon?limit=151");
        const results = pokemonListResponse.data.results;

        let pokemonDTOList = [];

        results.map(pokemon => {
            pokemonDTOList.push({
                id: results.indexOf(pokemon) + 1,
                name: pokemon.name,
                imageURL: "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/" + (results.indexOf(pokemon) + 1) + ".png",
            });
        });

        setPokemonList(pokemonDTOList);
    };

    return (
        <div className="App">
            <header>
                <div className={"title"}>
                    <CgPokemon/>
                    Pok√©Builder
                </div>
            </header>
            <div className={"content"}>
                <TeamBuilder setIsAdding={setIsAdding}/>
                {isAdding &&
                <div className={"pokePickModal"}>
                    <FilterBar/>
                    <PokemonList pokemonList={pokemonList}/>
                </div>
                }
            </div>
        </div>
    );
}

export default App;
