import "./PokemonList.css";
import Pokemon from "./Pokemon";
import {useEffect} from "react";

const PokemonList = ({pokemonData, setCurrentPokemon, currentTeam, setCurrentTeam}) => {

    const pokeList = pokemonData.map(pokemon => {
        return <Pokemon key={pokemon.id} pokemon={pokemon} setCurrentPokemon={setCurrentPokemon}
                        currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>;
    });

    useEffect(() => {
        let options = {
            root: null,
            rootMargin: "0px",
            threshold: 1.0,
        };
        let observer = new IntersectionObserver(handleObserver, options);
        observer.observe(document.querySelector(".PokemonList"));
    }, []);

    const handleObserver = (entries, observer) => {
        console.log(entries[0].isIntersecting);
        const y = entries[0].boundingClientRect.y;
    };

    return (
        <div className={"PokemonList"}>
            {pokeList}
        </div>
    )
}


export default PokemonList;
