import './App.css';
import TeamBuilder from "./Component/TeamBuilder";
import FilterBar from "./Component/FilterBar";
import PokemonList from "./Component/PokemonList";
import {CgPokemon} from "react-icons/cg"
import {useEffect, useState} from "react";
import axios from "axios";

function App() {

    const [pokemonData, setPokemonData] = useState([]);
    const [currentPokemon, setCurrentPokemon] = useState([]);
    const [isAdding, setIsAdding] = useState(false);

    useEffect(async () => {
        await fetchPokemon(1, 30);
    }, []);

    useEffect(() => {
       setCurrentPokemon(pokemonData.slice(1, 10))
    }, [pokemonData]);

    useEffect(() => {
        console.log(currentPokemon);
    }, [currentPokemon])

    const fetchPokemon = (start, count) => {
        axios.get("http://localhost:8080/pokemon?start=" + start + "&count=" +count)
            .then(pokemonDTOList => setPokemonData(pokemonDTOList.data));
    }

    return (
        <div className="App">
            <header>
                <div className={"title"}>
                    <CgPokemon/>
                    Pok√©Builder
                </div>
            </header>
            <div className={"content"}>
                <TeamBuilder/>
                <div className={"pokePickModal"}>
                    <FilterBar currentPokemon={currentPokemon} setCurrentPokemon={setCurrentPokemon}/>
                    <PokemonList currentPokemon={currentPokemon}/>
                </div>
            </div>
        </div>
    );
}

export default App;
