import "./FilterBar.css"
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";

const FilterBar = ({pokemonData, setPokemonData}) => {

    const [filterString, setFilterString] = useState("");
    const [selectedValue,setSelectedValue] = useState("n/a");
    const [filteredPokemon, setFilteredPokemon] = useState(starterData);

    // const handleNameFilter = event => {
    //     setPokemonData(pokemonData.filter(pokemon => pokemon.name.includes(filterString.toLowerCase())));
    // }

    const handleFilterChange = event => {
        setFilterString(event.target.value);
        setFilteredPokemon(filteredPokemon.filter(pokemon => pokemon.name.includes(filterString.toLowerCase())));
    }

    const handleResetFilter = () => {
        setPokemonData(starterData);
        setFilterString("");
        setSelectedValue("n/a");
    }

    const handleSelectValueChanged = event => {
        setSelectedValue(event.target.value);
        if(event.target.value === "n/a"){
            setPokemonData(starterData);
        }
        if(event.target.value === "I") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 1 && pokemon.id <= 151)));
        }
        if(event.target.value === "II") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 152 && pokemon.id <= 251)));
        }
        if(event.target.value === "III") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 252 && pokemon.id <= 386)));
        }
        if(event.target.value === "IV") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 387 && pokemon.id <= 493)));
        }
        if(event.target.value === "V") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 494 && pokemon.id <= 649)));
        }
        if(event.target.value === "VI") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 650 && pokemon.id <= 721)));
        }
        if(event.target.value === "VII") {
            setPokemonData(starterData.filter(pokemon => (pokemon.id >= 722 && pokemon.id <= 898)));
        }
    }

    return  (
      <div className={"filterBar"}>
          Name:
          <input
              className={"nameSearch"}
              type={"text"}
              placeholder={"Pikachu..."}
              value={filterString}
              onChange={event => handleFilterChange(event)}
              // onKeyPress={event => handleNameFilter(event)}
              />
          Generation:
          <select value={selectedValue} onChange={event => handleSelectValueChanged(event)}>
              <option>n/a</option>
              <option>I</option>
              <option>II</option>
              <option>III</option>
              <option>IV</option>
              <option>V</option>
              <option>VI</option>
              <option>VII</option>
          </select>
          <button onClick={() => handleResetFilter()}>Reset</button>
      </div>
    );
}

export default FilterBar;
