import "./Login.css";
import {useEffect, useState} from "react";
import axios from "axios";

const Login = ({loggedIn, setLoggedIn, setCurrentUser}) => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = (username, password) => {
        let user = {
            username: username,
            password: password,
        };

        axios.post("/user/login", user).then(response => {
            sessionStorage.setItem("loggedIn", response.data);
            sessionStorage.setItem("currentUser", user.username);
            setLoggedIn(response.data);
            setCurrentUser(user.username);
        })
            .then(() => setUsername(""))
            .then(() => setPassword(""));

    };

    const handleCreateUser = (username, password) => {
        axios.post("/user", {
            username: username,
            password: password,
        }).then(() => setUsername(""))
            .then(() => setPassword(""));
    };

    const handleUsernameChange = event => {
        setUsername(event.target.value);
    }

    const handlePasswordChange = event => {
        setPassword(event.target.value);
    }

    if (loggedIn) {
        return (
            <div style={{color: "white"}}>You're logged in!</div>
        );
    }

    return (
        <div className={"Login"}>
            <div className={"formRow"}>
                <label>Username </label>
                <input type={"text"} name={"username"} value={username}
                       onChange={event => handleUsernameChange(event)}/>
            </div>
            <div className={"formRow"}>
                <label>Password </label>
                <input type={"text"} name={"password"} value={password}
                       onChange={event => handlePasswordChange(event)}/>
            </div>
            <button
                className={"loginButton"}
                name={"login"}
                onClick={() => handleLogin(username, password)}
            >Login
            </button>
            <button
                className={"createButton"} name={"create"}
                onClick={() => handleCreateUser(username, password)}
            >Create
            </button>
        </div>
    );
}

export default Login;
