import {Radar} from "react-chartjs-2";

const RadarChart = ({chartData}) => {
    const options = {
        layout: {
            padding: "1rem",
        },
        plugins: {
            legend: {
                display: false,
            },
            tooltip: {
                enabled: true,
            },
        },
        elements: {
            line: {
                borderWidth: 1,
                borderColor: "white",
            },
            point: {
                radius: 2,
            },
        },
        scale: {
            display: false,
            backgroundColor: "white",
            min: 0,
            max: 160,
            grid : {
              borderColor: "red",
                borderWidth: 3,
            },
            ticks: {
                showLabelBackdrop: false,
            },
        },
    };

    return (
        <div>
            <Radar type={"radar"} data={chartData} options={options}/>
        </div>
    );
}

export default RadarChart;
