import "./PokeDetails.css"

const PokeDetails = ({currentPokemon, setCurrentPokemon}) => {

    const typeList = currentPokemon.types.map(typeSlot => {
        return <div key={`${currentPokemon.id}_${typeSlot.slot}`} className={typeSlot.type.name}>{typeSlot.type.name}</div>
    });

    return (
        <div className={"PokeDetails"}>
            <button className={"detailsCloseButton"} onClick={() => setCurrentPokemon({})}>X</button>
            <div className={"detailRow1"}>
                <img className={"pokemonDetailsImage"} src={currentPokemon.image}/>
                {typeList}
                <div className={"pokemonData"}>
                    <div className={"pokemonName"}>{currentPokemon.name[0].toUpperCase() + currentPokemon.name.slice(1)}</div>
                </div>
            </div>
        </div>
    );
}

export default PokeDetails;
