import './App.css';
import {Link, NavLink, Outlet} from "react-router-dom";
import {CgPokemon} from "react-icons/cg";
import {useState} from "react";

const App = () => {

    const [viewingHome, setViewingHome] = useState(true);

    return (
        <div className={"App"}>
            <header className={"header"}>
                <div className={"title"}>
                    <Link className={"title"} to={"/"} onClick={() => setViewingHome(true)}>
                        <CgPokemon/>
                        <div>Pok√©Builder</div>
                    </Link>
                </div>
                <div className={"navBar"}>
                    <NavLink className={({isActive}) => isActive ? "activeLink" : "inactiveLink"} to={"/build"}
                             onClick={() => setViewingHome(false)}>Build</NavLink>
                    <NavLink className={({isActive}) => isActive ? "activeLink" : "inactiveLink"}
                             to={"/teams"}
                             onClick={() => setViewingHome(false)}>Teams</NavLink>
                    <NavLink className={({isActive}) => isActive ? "activeLink" : "inactiveLink"}
                             to={"/login"}
                             onClick={() => setViewingHome(false)}>Login</NavLink>
                </div>
            </header>
            {viewingHome ?
                <div style={{color: "white"}}> HOMEPAGE </div>
                :
                <Outlet/>
            }
        </div>
    );
}

export default App;
