import './App.css';
import {Link, NavLink, Outlet} from "react-router-dom";
import {CgPokemon} from "react-icons/cg";
import {useState} from "react";

const App = () => {

    const [showLandingPage, setShowLandingPage] = useState(true);

    return (
        <div className={"App"}>
            {showLandingPage ?
                <div className={"landingPage"}>
                    <img className={"landingPageImage"}
                         src={"https://media.giphy.com/media/zdTzJbClt8WDEB3Wav/giphy.gif"}/>
                    <div className={"landingPageTitle"}>
                        <CgPokemon/>
                        <div>PokéBuilder</div>
                    </div>
                    <div className={"landingPageContent"}>a tool for building and sharing Pokémon teams</div>
                    <Link className={"getStarted"} to={"/build"} onClick={() => setShowLandingPage(false)}>Get
                        Started</Link>
                </div>
                :
                <div>
                    <header className={"header"}>
                        <div className={"title"}>
                            <CgPokemon/>
                            <div>PokéBuilder</div>
                        </div>
                        <div className={"navBar"}>
                            <NavLink className={({isActive}) => isActive ? "activeLink" : "inactiveLink"}
                                     to={"/build"}>Build</NavLink>
                            <NavLink className={({isActive}) => isActive ? "activeLink" : "inactiveLink"}
                                     to={"/teams"}>Teams</NavLink>
                            <NavLink className={({isActive}) => isActive ? "activeLink" : "inactiveLink"}
                                     to={"/login"}>Login</NavLink>
                        </div>
                    </header>
                    <Outlet/>
                </div>
            }
        </div>
    );
}

export default App;
