import "./PokemonList.css";
import Pokemon from "./Pokemon";
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";

const PokemonList = ({pokemonData, setPokemonData, setCurrentPokemon, currentTeam, setCurrentTeam}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [isLoading, setIsLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true);

    const pokeList = pokemonData.map(pokemon => {
        return <Pokemon key={pokemon.id} pokemon={pokemon} setCurrentPokemon={setCurrentPokemon}
                        currentTeam={currentTeam} setCurrentTeam={setCurrentTeam}/>;
    });

    useEffect(() => {
        document.getElementById("PokemonList").addEventListener("scroll", loadMore);
    }, []);

    const loadMore = () => {
        if (isLoading || !hasMore) return;

        setIsLoading(true);
        if (pageNumber >= 17) setHasMore(false);
        const scrollList = document.getElementById("PokemonList");

        if (scrollList.scrollTop  >= scrollList.scrollHeight * .8) {
            console.log("loading more");
            setPageNumber(pageNumber + 1);
            setPokemonData(starterData.slice(0, pageNumber * 50));
        }
        setIsLoading(false);
    }

    return (
        <div id={"PokemonList"}>
            {pokeList}
        </div>
    )
}


export default PokemonList;
