import "./PokeDetails.css"
import {useEffect, useState} from "react";
import {CgPokemon} from "react-icons/cg"
import axios from "axios";
import RadarChart from "./RadarChart";

const PokeDetails = ({currentPokemon, setCurrentPokemon}) => {

    const [moves, setMoves] = useState([]);
    const [abilities, setAbilities] = useState([]);
    const [stats, setStats] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        await axios.get("http://localhost:8080/pokemon/" + currentPokemon.id + "/moves")
            .then(response => setMoves(response.data));

        await axios.get("http://localhost:8080/pokemon/" + currentPokemon.id + "/abilities")
            .then(response => setAbilities(response.data));

        await axios.get("http://localhost:8080/pokemon/" + currentPokemon.id + "/stats")
            .then(response => setStats(response.data));
    }

    const statData = {
        labels: ["HP", "Attack", "Defense", "Speed", "Sp. Defense", "Sp. Attack"],
        datasets: [
            {
                label: "",
                data: [stats[0].base_stat, stats[1].base_stat, stats[2].base_stat, stats[5].base_stat, stats[4].base_stat, stats[3].base_stat]
            }
        ],
    };

    const typeList = currentPokemon.types.map(typeSlot => {
        return <div key={`${currentPokemon.id}_${typeSlot.slot}`}
                    className={typeSlot.type.name}>{typeSlot.type.name}</div>
    });

    const abilityList = abilities.map(abilitySlot => {
        return <div key={`${currentPokemon.id}_${abilitySlot.slot}`}
                    className={"abilityName"}
        >
            {abilitySlot.ability.name}
        </div>
    })

    return (
        <div className={"PokeDetails"}>
            <button className={"detailsCloseButton"} onClick={() => setCurrentPokemon({})}>X</button>
            <div className={"detailRow1"}>
                <div className={"imageWithType"}>
                    <img className={"pokemonDetailsImage"} src={currentPokemon.image}/>
                    <div className={"types"}>{typeList}</div>
                </div>
                <div className={"pokemonData"}>
                    <div className={"pokemonName"}>
                        <div className={"pokemonNumber"}>
                            <CgPokemon/>
                            {currentPokemon.id}
                        </div>
                        {currentPokemon.name[0].toUpperCase() + currentPokemon.name.slice(1)}
                    </div>
                    <div className={"heightWeight"}>
                        <h3>Height:</h3> {currentPokemon.height/10} m
                        <h3>Weight:</h3> {currentPokemon.weight/10} kg
                    </div>
                    <div className={"abilities"}>
                        <h3>Abilities:</h3>
                        {abilityList}
                    </div>
                </div>
                <RadarChart chartData={statData}/>
            </div>
        </div>
    );
}

export default PokeDetails;
