import "./FilterBar.css"
import {useEffect, useState} from "react";
import {starterData} from "../data/starterData";

const FilterBar = ({setPokemonData}) => {

    const [filterString, setFilterString] = useState("");
    const [selectedValue,setSelectedValue] = useState("I");

    useEffect(() => {
        console.log(filterString);
    }, [filterString]);

    const handleNameFilter = event => {
        if (event.key === 'Enter') {
            setPokemonData(starterData.filter(pokemon => pokemon.name.includes(filterString.toLowerCase())));
            setFilterString("");
        }
    }

    const handleFilterChange = event => {
        setFilterString(event.target.value);
    }

    const handleResetFilter = () => {
        setPokemonData(starterData);
    }

    return  (
      <div className={"filterBar"}>
          Name:
          <input
              className={"nameSearch"}
              type={"text"}
              placeholder={"Pikachu..."}
              value={filterString}
              onChange={event => handleFilterChange(event)}
              onKeyPress={event => handleNameFilter(event)}/>
          Generation:
          <select value={selectedValue}>
              <option>I</option>
              <option>II</option>
              <option>III</option>
              <option>IV</option>
              <option>V</option>
              <option>VI</option>
              <option>VII</option>
          </select>
          <button onClick={() => handleResetFilter()}>Reset</button>
      </div>
    );
}

export default FilterBar;
